// Playlist.js
import React from 'react';
import LibraryListItem from './Components/Library/LibraryListItem';
import './Playlist.css'; // Import the Playlist.css file

function Playlist({ playlist, setMusicIndex, currentAudio, isAudioPlaying, setSongState }) {
  const nowPlayingSongIndex = playlist.findIndex(song => song.songSrc === currentAudio.current.src);

  return (
    <div className="playlist">
      <h2>Playlist</h2>
      <div className="playlist__list">
        {/* Add the "scrollable" class to control the height and scrollbar */}
        <div className="playlist__scrollable">
          {playlist.map((song, index) => (
            <LibraryListItem
              key={index}
              song={song}
              setMusicIndex={setMusicIndex}
              currentAudio={currentAudio}
              isAudioPlaying={isAudioPlaying}
              setSongState={setSongState}
              isNowPlaying={index === nowPlayingSongIndex}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Playlist;
